import { keyboardImplementationWrapper } from "@testing-library/user-event/dist/keyboard/index.js";
import {isLowercaseLatin, isUppercaseLatin, getLowercaseLetterNumber, shiftLowercaseLatin, shiftUppercaseLatin, getUppercaseLetterByNumber} from "./helper_functions.js"

export function Compress(text, key)
{
    let result = "";
    let keyIndex = 0;

    for(let i = 0; i < text.length; i++)
    {
        let letter = key[keyIndex++ % key.length];
        let shift;
        if (isLowercaseLatin(letter))
            shift = getLowercaseLetterNumber(letter);
        else if (isUppercaseLatin(letter))
            shift = getUppercaseLetterByNumber(letter);
        else
            shift = 0;

        if (isLowercaseLatin(letter))
        {
            result += shiftLowercaseLatin(letter, shift);
        }
        else if (isUppercaseLatin(letter))
        {
            result += shiftUppercaseLatin(letter, shift);
        }
        else 
        {
            keyIndex--;
            result += letter;
        }
    }

    return result;
}

