import {isLowercaseLatin, isUppercaseLatin, getLowercaseLetterNumber, shiftLowercaseLatin, shiftUppercaseLatin} from "./helper_functions.js"

export function Compress(text, key)
{
    let result = "";
    let keyIndex = 0;

    for(let i = 0; i < text.length; i++)
    {
        if (isLowercaseLatin(letter))
        {
            let shift = getLowercaseLetterNumber(key[keyIndex++ % key.length]);
            result += shiftLowercaseLatin(letter, shift);
        }
        else if (isUppercaseLatin(letter))
        {
            let shift = getLowercaseLetterNumber(key[keyIndex++ % key.length]);
            result += shiftUppercaseLatin(letter, shift);
        }
        else 
        {
            result += letter;
        }
    }

    return result;
}

export function Decompress(text, key)
{
    let result = "";
    let keyIndex = 0;

    for(let i = 0; i < text.length; i++)
    {
        if (isLowercaseLatin(letter))
        {
            let shift = getLowercaseLetterNumber(key[keyIndex++ % key.length]);
            result += shiftLowercaseLatin(letter, -shift);
        }
        else if (isUppercaseLatin(letter))
        {
            let shift = getLowercaseLetterNumber(key[keyIndex++ % key.length]);
            result += shiftUppercaseLatin(letter, -shift);
        }
        else 
        {
            result += letter;
        }
    }

    return result;
}
